mapper.select:
    select count(distinct sl.sid)
	from subjectLog sl ,course_subject cs, course c
	where sl.sid = cs.sid and cs.cid = c.id
		and sl.userid =#{userid,jdbcType=VARCHAR}
		and c.id = #{cid,jdbcType=INTEGER}

select 

from course t


		t.pid = #{pid,jdbcType=VARCHAR}


		and t.type = #{type,jdbcType=VARCHAR}

select 

from course t
where t.id = #{id,jdbcType=INTEGER}
